sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if ((isLoading && !g_form.isNewRecord()) || newValue === '' || newValue == oldValue) {
        return;
    }
    var ga2 = new GlideAjax('GetEmailAddress');
    ga2.addParam('sysparm_name', 'getEmail');
    ga2.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
    alert('reached here');
    ga2.getXMLAnswer(fillEmail);
    alert('made call');
}

function fillEmail(email) {
    // 	email = email.responseXML.documentElement.getAttribute('answer');
    alert(email);
    g_form.clearValue('u_requested_for_email');
    g_form.setValue('u_requested_for_email', email);
}
